import { useEffect, useState } from "react"

export interface Settings {
  download_directory: string
}

const DEFAULT_SETTINGS: Settings = {
  download_directory: "xyz",
}

const SETTINGS_STORAGE_KEY = "settings"

export interface SettingsError extends Error {
  code: "STORAGE_ERROR" | "VALIDATION_ERROR"
  details?: unknown
}

export interface SettingsValidation {
  isValid: boolean
  errors: string[]
}

export interface SettingsResult {
  success: boolean
  data?: Settings
  error: SettingsError | null
}

interface UseSettingsReturn {
  settings: Settings
  loading: boolean
  error: SettingsError | null
  updateSettings: (newSettings: Partial<Settings>) => Promise<SettingsResult>
}

export function useChromeStorage<T = unknown>(key: string) {
  console.log("useChromeStorage(%s)", key)
  const [data, setData] = useState<T | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<unknown>(null)

  const setStorageData = async (newData: T) => {
    console.log("useChromeStorage(%s) setting data:", key, newData)
    setLoading(true)
    setError(null)
    try {
      await chrome.storage.local.set({ [key]: newData })
      setData(newData)
    } catch (err) {
      console.error("useChromeStorage(%s) set error:", key, err)
      setError(err)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    let isMounted = true

    const runEffect = async () => {
      console.log("useChromeStorage(%s) fetching data", key)
      setLoading(true)
      setError(null)

      try {
        const result = await chrome.storage.local.get(key)
        console.log(
          "useChromeStorage(%s) got result:",
          key,
          JSON.stringify(result)
        )
        if (isMounted) {
          setData(result[key] ?? null)
        }
      } catch (err) {
        console.error("useChromeStorage(%s) error:", key, err)
        if (isMounted) {
          setError(err)
        }
      } finally {
        if (isMounted) {
          setLoading(false)
        }
      }
    }

    runEffect()
    return () => {
      isMounted = false
    }
  }, [key])

  return { data, loading, error, setData: setStorageData }
}

export function useSettings(): UseSettingsReturn {
  const {
    data: settingsData,
    loading,
    error: chromeStorageError,
    setData: setSettingsData,
  } = useChromeStorage<Settings>(SETTINGS_STORAGE_KEY)
  const [settings, setSettings] = useState<Settings>(DEFAULT_SETTINGS)
  const [error, setError] = useState<SettingsError | null>(null)

  useEffect(() => {
    if (chromeStorageError) {
      const settingsError = new Error("Storage error") as SettingsError
      settingsError.code = "STORAGE_ERROR"
      settingsError.details = chromeStorageError
      setError(settingsError)
    } else {
      setError(null)
    }
  }, [chromeStorageError])

  useEffect(() => {
    if (settingsData) {
      console.log("useSettings: loaded settings:", settingsData)
      setSettings(settingsData)
    }
  }, [settingsData])

  const validateSettings = (settings: Settings): SettingsValidation => {
    const result: SettingsValidation = { isValid: true, errors: [] }

    if (!settings.download_directory) {
      result.errors.push("Download directory is required")
    }

    if (result.errors.length !== 0) {
      result.isValid = false
    }

    return result
  }

  const updateSettings = async (
    newSettings: Partial<Settings>
  ): Promise<SettingsResult> => {
    const updatedSettings = { ...settings, ...newSettings }
    const validation = validateSettings(updatedSettings)

    if (!validation.isValid) {
      const error = new Error("Invalid settings") as SettingsError

      error.code = "VALIDATION_ERROR"
      error.details = validation.errors

      return { success: false, error }
    }

    await setSettingsData(updatedSettings)
    if (chromeStorageError) {
      const error = new Error("Storage error") as SettingsError

      error.code = "STORAGE_ERROR"
      error.details = chromeStorageError
      setError(error)
      return { success: false, error }
    }

    setError(null)
    setSettings(updatedSettings)
    return { success: true, data: updatedSettings, error: null }
  }

  return { settings, loading, error, updateSettings }
}
