const openSidePanel = (tab: chrome.tabs.Tab): void => {
  chrome.sidePanel.open({ windowId: tab.windowId })
}

chrome.action.onClicked.addListener((tab) => {
  openSidePanel(tab)
})

chrome.commands.onCommand.addListener((command, tab) => {
  if (command === "open-side-panel") {
    openSidePanel(tab)
  } else {
    console.log("command not found:", command)
  }
})

import { useState, useEffect } from "react"
import { useSettings } from "../../hooks/useSettings"
import styles from "./Settings.module.css"

interface SettingsFormState {
  downloadDirectory: string
  isDirty: boolean
  isValid: boolean
  errors: string[]
}

export const Settings = () => {
  const {
    settings,
    loading: settingsLoading,
    error: settingsError,
    updateSettings,
  } = useSettings()

  const [formState, setFormState] = useState<SettingsFormState>({
    downloadDirectory: settings.download_directory,
    isDirty: false,
    isValid: true,
    errors: [],
  })

  useEffect(() => {
    setFormState((prev) => ({
      ...prev,
      downloadDirectory: settings.download_directory,
      isDirty: false,
      errors: [],
    }))
  }, [settings.download_directory])

  const saveSettings = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()

    const result = await updateSettings({
      download_directory: formState.downloadDirectory,
    })

    if (result.success) {
      setFormState((prev) => ({ ...prev, isDirty: false, errors: [] }))
    } else if (result.error?.code === "VALIDATION_ERROR") {
      setFormState((prev) => ({
        ...prev,
        isValid: false,
        errors: result.error!.details as string[],
      }))
    }
  }

  if (settingsLoading) return <p>Loading...</p>
  if (settingsError) {
    const errorMessage =
      settingsError.code === "VALIDATION_ERROR"
        ? `Validation error: ${(settingsError.details as string[]).join(", ")}`
        : `Error: ${settingsError.message}`
    console.error("Settings error:", JSON.stringify(settingsError))
    return <p className={styles.error}>{errorMessage}</p>
  }

  return (
    <div className={styles.container}>
      <h1>Settings</h1>

      <form onSubmit={saveSettings}>
        <div className={styles.setting}>
          <label htmlFor="download-directory-field">Download Directory</label>
          <input
            id="download-directory-field"
            className={`${styles.setting} ${
              !formState.isValid ? styles.error : ""
            }`}
            value={formState.downloadDirectory}
            onChange={(event) =>
              setFormState((prev) => ({
                ...prev,
                downloadDirectory: event.target.value,
                isDirty: true,
                isValid: true,
                errors: [],
              }))
            }
          />
          {formState.errors.length > 0 && (
            <p className={styles.error}>{formState.errors.join(", ")}</p>
          )}
        </div>
        <button type="submit" disabled={!formState.isDirty}>
          Save
        </button>
      </form>
    </div>
  )
}

