import { useState, useEffect } from "react"
import { useSettings } from "../../hooks/useSettings"
import styles from "./Settings.module.css"

export const Settings = () => {
  const {
    settings,
    loading: settingsLoading,
    error: settingsError,
    updateSettings,
  } = useSettings()

  const [formData, setFormData] = useState({
    downloadDirectory: settings.download_directory,
  })
  const [isDirty, setIsDirty] = useState(false)

  useEffect(() => {
    setFormData({
      downloadDirectory: settings.download_directory,
    })
    setIsDirty(false)
  }, [settings.download_directory])

  const saveSettings = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()
    await updateSettings({
      download_directory: formData.downloadDirectory,
    })
    setIsDirty(false)
  }

  if (settingsLoading) {
    return <p>Loading...</p>
  }
  if (settingsError) {
    console.error("Settings error:", settingsError)
    return <p className={styles.error}>Error: {settingsError}</p>
  }

  return (
    <div className={styles.container}>
      <h1>Settings</h1>

      <form onSubmit={saveSettings}>
        <div className={styles.setting}>
          <label htmlFor="download-directory-field">Download Directory</label>
          <input
            id="download-directory-field"
            className={styles.setting}
            value={formData.downloadDirectory}
            onChange={(event) => {
              setFormData({
                ...formData,
                downloadDirectory: event.target.value,
              })
              setIsDirty(true)
            }}
          />
        </div>
        <button type="submit" disabled={!isDirty}>
          Save
        </button>
      </form>
    </div>
  )
}

