import "@mantine/core/styles.css"

import { MantineProvider } from "@mantine/core"
import { theme } from "./theme/default"
import { SettingsTab } from "./components/settings_tab/SettingsTab"
import { DownloadsTab } from "./components/downloads_tab/DownloadsTab"
import { Tabs } from "./components/tabs/Tabs"

export function App() {
  const tabs = [
    {
      value: "downloads",
      label: "Downloads",
      content: <DownloadsTab />
    },
    {
      value: "settings",
      label: "Settings",
      content: <SettingsTab />
    }
  ]

  return (
    <MantineProvider theme={theme} defaultColorScheme="auto">
      <Tabs tabs={tabs} defaultTab="downloads" />
    </MantineProvider>
  )
}

import { Container, Stack, Title, Text } from "@mantine/core"

export const DownloadsTab = () => {
  return (
    <Container>
      <Stack>
        <Title order={1}>Downloads</Title>
        <Text>No downloads yet</Text>
      </Stack>
    </Container>
  )
}

import debug from "debug"

const logger = debug("background")

logger("background.js loaded 4")

const openSidePanel = (tab: chrome.tabs.Tab): void => {
  chrome.sidePanel.open({ windowId: tab.windowId })
}

chrome.action.onClicked.addListener((tab) => {
  openSidePanel(tab)
})

chrome.commands.onCommand.addListener((command, tab) => {
  if (command === "open-side-panel") {
    openSidePanel(tab)
  } else {
    logger("command not found:", command)
  }
})

