import { useEffect, useState } from "react"
import debug from "debug"
import type { StringError } from "../types/errors"

export interface Settings {
  download_directory: string
}

const DEFAULT_SETTINGS: Settings = {
  download_directory: "xyz",
}

const SETTINGS_STORAGE_KEY = "settings"
const logger = debug("settings")

interface UseSettingsReturn {
  settings: Settings
  loading: boolean
  error: StringError
  updateSettings: (newSettings: Partial<Settings>) => Promise<void>
}

export function useChromeStorage<T = unknown>(key: string) {
  logger("useChromeStorage(%s)", key)
  const [data, setData] = useState<T | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<StringError>(null)

  const setStorageData = async (newData: T) => {
    logger("useChromeStorage(%s) setting data:", key, newData)
    setLoading(true)
    setError(null)
    try {
      await chrome.storage.local.set({ [key]: newData })
      setData(newData)
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : String(err)
      setError(errorMessage)
      throw err // Re-throw so caller can handle it
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    const runEffect = async () => {
      logger("useChromeStorage(%s) fetching data", key)
      setLoading(true)
      setError(null)

      try {
        const result = await chrome.storage.local.get(key)
        logger("useChromeStorage(%s) got result:", key, JSON.stringify(result))
        setData(result[key] ?? null)
      } catch (err) {
        setError(err instanceof Error ? err.message : String(err))
      } finally {
        setLoading(false)
      }
    }

    runEffect()
  }, [key])

  return { data, loading, error, setData: setStorageData }
}

export function useSettings(): UseSettingsReturn {
  const {
    data: settingsData,
    loading,
    error: storageError,
    setData: setSettingsData,
  } = useChromeStorage<Settings>(SETTINGS_STORAGE_KEY)
  const [settings, setSettings] = useState<Settings>(DEFAULT_SETTINGS)
  const [error, setError] = useState<StringError>(null)

  useEffect(() => {
    if (settingsData) {
      logger("useSettings: loaded settings:", settingsData)
      setSettings(settingsData)
    }
  }, [settingsData])

  const updateSettings = async (
    newSettings: Partial<Settings>
  ): Promise<void> => {
    const updatedSettings = { ...settings, ...newSettings }

    // Basic validation
    if (!updatedSettings.download_directory) {
      setError("Download directory is required")
      return
    }

    try {
      await setSettingsData(updatedSettings)
      setError(null)
      setSettings(updatedSettings)
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : String(err)
      setError(`Failed to save settings: ${errorMessage}`)
    }
  }

  // Combine storage and update errors
  const combinedError = error || storageError

  return { settings, loading, error: combinedError, updateSettings }
}
